import React, { Component} from 'react';
import { Card, Form, Button} from 'react-bootstrap';

export default class Couriers extends Component {
    state = {
        couriers:[{ firstName: "", lastName: "" }]
    }

    add = () =>{
        this.setState(prevState => ({
            arrayvar: [...prevState.arrayvar, newelement]
          }))
    }

    render(){

        return(
            <Card border="primary" className="m-5 pt-3">
                <Card.Title className= "mx-auto"><h4> Couriers</h4> </Card.Title>
                <Card.Body className="m-5">
      
                {this.state.couriers.map((x, i) => {
                    return (
                        <div>
                            <Card border="primary" >
                                <Card.Title className= "mx-auto"> Courier {i+1} </Card.Title>
                                <Card.Body>
                                    <Form>
                                        <Form.Group className="mb-3" controlId="formBasicEmail">
                                            <Form.Label>Email address</Form.Label>
                                            <Form.Control type="email" placeholder="Enter email" value={x.firstName} />
                                        </Form.Group>

                                        <Form.Group className="mb-3" controlId="formBasicEmail">
                                            <Form.Label>Email address</Form.Label>
                                            <Form.Control type="email" placeholder="Enter email" value={x.firstName} />
                                        </Form.Group>
                                        
                                        <div className="btn-box">
                                            {this.state.couriers.length !== 1 && <Button variant="danger">Remove</Button>}
                                        </div>
                                    </Form>
                                </Card.Body>
                            </Card>
                            {this.state.couriers.length - 1 === i && <Button className="mt-3"  onClick={this.add}> variant="primary">Add courier</Button>}
                        </div>
                    );
                })}
                </Card.Body>
            </Card>
        );
    }
}
