import React, { Component} from 'react';
import { Card, Row, Col, Form, Button} from 'react-bootstrap';

export default class Configuration extends Component {
    state = {
        maxDeliveryTimeMinutes: 40,
        courierSpeedKmH: 20 
    }


    render(){
        return(
            <Card border="primary" className="m-5 mt-1 pt-4">
                <div className='text-center mb-1'>
                    <h4> Configuration:</h4>
                </div>
                <div className='text-center mb-2'>
                    Enter the configuration data:
                </div>
                <Card.Body>
                    <div>
                        <Form.Group as={Row} className="mb-1 justify-content-md-center" controlId="formHorizontalNumber">
                            <Form.Label column sm={3}>Max delivery time minutes:</Form.Label>
                            <Col sm={2}>
                                <Form.Control type="number" step="0.01" value={this.state.configuration.maxDeliveryTimeMinutes} onChange={this.handleLonChange} />
                            </Col>
                        </Form.Group>

                        <Form.Group as={Row} className="mb-1 justify-content-md-center" controlId="formHorizontalNumber">
                            <Form.Label column sm={3}>Courier Speed in Km/H:</Form.Label>
                            <Col sm={2}>
                                <Form.Control type="number" step="0.01" value={this.state.courierSpeedKmH} onChange={this.handleLonChange} />
                            </Col>
                        </Form.Group>
                    </div>
                </Card.Body>
            </Card>
        );
    }
}
