import React, { Component} from 'react';
import { Card, Row, Col, Form, Button} from 'react-bootstrap';

export default class Orders extends Component {

    state = {
        orders:[{ name: "", pickLat: 45.0, pickLon: 20.0, delLat: 46.0, delLon: 21.0  }]
    }

    add = () =>{
        this.setState(prevState  => ({
            orders: [...prevState.orders, { name: "", pickLat: 45.0, pickLon: 20.0, delLat: 46.0, delLon: 21.0 }]
        }))   
    }

    remove = (id) =>{
        var orders =  [...this.state.orders];
        orders.splice(id, 1);
        this.setState({orders:orders})
    }

    handleNameChange = (event, id) => {
        event.preventDefault();
        var orders =  [...this.state.orders];
        orders[id].name = event.target.value;
        this.setState({orders:orders})
    }

    handlepickLatChange = (event, id) => {
        event.preventDefault();
        var orders =  [...this.state.orders];
        orders[id].pickLat = event.target.value;
        this.setState({orders:orders})
    }

    handlepickLonChange = (event, id) => {
        event.preventDefault();
        var orders =  [...this.state.orders];
        orders[id].pickLon = event.target.value;
        this.setState({orders:orders})
    }


    handledelLatChange = (event, id) => {
        event.preventDefault();
        var orders =  [...this.state.orders];
        orders[id].pickLat = event.target.value;
        this.setState({orders:orders})
    }

    handledelLonChange = (event, id) => {
        event.preventDefault();
        var orders =  [...this.state.orders];
        orders[id].pickLon = event.target.value;
        this.setState({orders:orders})
    }

    render(){

        return(
            <Card border="primary" className="m-5 pt-3">
                <Card.Body className="m-1">
                <div className='text-center mb-1'>
                    <h4> Orders:</h4>
                </div>
                <div className='text-center mb-2'>
                    Enter the orders data:
                </div>
                {this.state.orders.map((x, i) => {
                    return (
                        <div>
                            <Card border="primary"  >
                                <div className="mt-1"><b>{i+1}.</b></div>
                                <Card.Body>
                                    <Form.Group as={Row} className="mb-1" controlId="formHorizontalEmail">
                                        <Form.Label column sm={2}>Name:</Form.Label>
                                        <Col sm={10}>
                                            <Form.Control type="email" placeholder="Enter courier name" value={x.name} onChange={(e) => this.handleNameChange(e,i)} />
                                        </Col>
                                    </Form.Group>

                                    <Form.Group as={Row} className="mb-1" controlId="formHorizontalNumber">
                                        <Form.Label column sm={4}>Pickup Lat:</Form.Label>
                                        <Col sm={8}>
                                            <Form.Control type="number" step="0.01" value={x.pickLat} onChange={(e) => this.handlepickLatChange(e,i)}/>
                                        </Col>
                                    </Form.Group>

                                    <Form.Group as={Row} className="mb-1" controlId="formHorizontalNumber">
                                        <Form.Label column sm={4}>Pickup Lon:</Form.Label>
                                        <Col sm={8}>
                                            <Form.Control type="number" step="0.01" value={x.pickLon} onChange={(e) => this.handlepickLonChange(e,i)} />
                                        </Col>
                                    </Form.Group>

                                    <Form.Group as={Row} className="mb-1" controlId="formHorizontalNumber">
                                        <Form.Label column sm={4}>Delivery Lat:</Form.Label>
                                        <Col sm={8}>
                                            <Form.Control type="number" step="0.01" value={x.delLat} onChange={(e) => this.handledelLatChange(e,i)}/>
                                        </Col>
                                    </Form.Group>

                                    <Form.Group as={Row} className="mb-1" controlId="formHorizontalNumber">
                                        <Form.Label column sm={4}>Delivery Lon:</Form.Label>
                                        <Col sm={8}>
                                            <Form.Control type="number" step="0.01" value={x.delLat} onChange={(e) => this.handledelLonChange(e,i)} />
                                        </Col>
                                    </Form.Group>
                                    
                                    <div className="btn-box text-center">
                                        {this.state.orders.length !== 1 && <Button variant="danger" onClick={() => this.remove(i)}>Remove</Button>}
                                    </div>
                                </Card.Body>
                            </Card>
                            {this.state.orders.length - 1 === i && <Button className="mt-3"  onClick={this.add} variant="primary">Add courier</Button>}
                        </div>
                    );
                })}
                </Card.Body>
            </Card>
        );
    }
}
