import React, { Component} from 'react';
import { Card, Form, Button} from 'react-bootstrap';
import NumericInput from 'react-numeric-input';

export default class Couriers extends Component {

    state = {
        couriers:[{ name: "", lat: 0, lon: 0  }]
    }

    add = () =>{
        this.setState(prevState  => ({
            couriers: [...prevState.couriers, { name: "", lat: 0, lon: 0  }]
        }))   
    }

    remove = (id) =>{
        var couriers =  [...this.state.couriers];
        couriers.splice(id, 1);
        this.setState({couriers:couriers})
    }

    handleEmailChange = (event, id) => {
        event.preventDefault();
        var couriers =  [...this.state.couriers];
        couriers[id].name = event.target.value;
        this.setState({couriers:couriers})
    }

    render(){

        return(
            <Card border="primary" className="m-5 pt-3">
                <Card.Title className= "mx-auto"><h4> Couriers:</h4> </Card.Title>
                <Card.Body className="m-1">
      
                {this.state.couriers.map((x, i) => {
                    return (
                        <div>
                            <Card border="primary" className="mb-3"  >
                                <Card.Title className= "mx-auto"> Courier {i+1} </Card.Title>
                                <Card.Body>
                                    <Form.Group className="mb-1" controlId="formBasicEmail">
                                        <Form.Label>Email address</Form.Label>
                                        <Form.Control type="email" placeholder="Enter email" value={x.name} onChange={(e) => this.handleEmailChange(e,i)} />
                                    </Form.Group>

                                    <Form.Group className="mb-1" controlId="formBasicNumber">
                                        <Form.Label>Lat:</Form.Label>
                                        <Form.Control type="number"value={x.lat} />
                                    </Form.Group>
                                    
                                    <div className="btn-box">
                                        {this.state.couriers.length !== 1 && <Button variant="danger" onClick={() => this.remove(i)}>Remove</Button>}
                                    </div>
                                </Card.Body>
                            </Card>
                            {this.state.couriers.length - 1 === i && <Button className="mt-3"  onClick={this.add} variant="primary">Add courier</Button>}
                        </div>
                    );
                })}
                </Card.Body>
            </Card>
        );
    }
}
