import React, { Component} from 'react';
import { Card } from 'react-bootstrap';

export default class Couriers extends Component {
    state = {
        couriers:[{ firstName: "", lastName: "" }]
    }

    render(){

        return(
            <Card border="primary" className="m-5 pt-3 p-5">
                <Card.Title className= "mx-auto"><h4> Couriers</h4> </Card.Title>
                <Card.Body className="m-5">
      
                {this.state.couriers.map((x, i) => {
                    return (
                    <div className="box">
                        <input name="firstName" value={x.firstName}/>
                        <input className="ml10" name="lastName" value={x.lastName}/>
                        <div className="btn-box">
                            {this.state.couriers.length !== 1 && <button className="mr10">Remove</button>}
                            {this.state.couriers.length - 1 === i && <button>Add</button>}
                        </div>
                    </div>
                    );
                })}
                </Card.Body>
            </Card>
        );
    }
}
